mapper.insert:

insert into SM_BUSINESS_TB (BUSINESS_ID, BUSINESS_NAME, BUSINESS_DESC, 
  IS_NEED_PROCESS, WHERE_STRING, NEED_PACKAGE, 
  PACKAGE_ORGAN, PRIVNAME, KEEP_YEARS
  )
values (#{businessId,jdbcType=DECIMAL}, #{businessName,jdbcType=VARCHAR}, #{businessDesc,jdbcType=VARCHAR}, 
  #{isNeedProcess,jdbcType=CHAR}, #{whereString,jdbcType=VARCHAR}, #{needPackage,jdbcType=CHAR}, 
  #{packageOrgan,jdbcType=CHAR}, #{privname,jdbcType=VARCHAR}, #{keepYears,jdbcType=VARCHAR}
  )

mapper.select:
	select  from SM_BUSINESS_TB


      BUSINESS_ID = #{businessId,jdbcType=VARCHAR}


      AND BUSINESS_NAME = #{businessName,jdbcType=VARCHAR}


      AND BUSINESS_DESC = #{businessDesc,jdbcType=VARCHAR}


      AND IS_NEED_PROCESS = #{isNeedProcess,jdbcType=CHAR}


      AND WHERE_STRING = #{whereString,jdbcType=VARCHAR}


      AND NEED_PACKAGE = #{needPackage,jdbcType=CHAR}


      AND PACKAGE_ORGAN = #{packageOrgan,jdbcType=CHAR}


      AND PRIVNAME = #{privname,jdbcType=VARCHAR}


      AND KEEP_YEARS = #{keepYears,jdbcType=VARCHAR}


	order by BUSINESS_ID
