mapper.select:

select 

from SM_DICTIONARY_TB
where FIELD_NAME = #{fieldName,jdbcType=VARCHAR}
  and CODE = #{code,jdbcType=VARCHAR}

mapper.delete:

delete from SM_DICTIONARY_TB
where FIELD_NAME = #{fieldName,jdbcType=VARCHAR}
  and CODE = #{code,jdbcType=VARCHAR}

mapper.update:

update SM_DICTIONARY_TB


    CONTENT = #{content,jdbcType=VARCHAR},


    PARENT_FIELD_NAME = #{parentFieldName,jdbcType=VARCHAR},


    PARENT_FIELD_CODE = #{parentFieldCode,jdbcType=VARCHAR},


where FIELD_NAME = #{fieldName,jdbcType=VARCHAR}
  and CODE = #{code,jdbcType=VARCHAR}


update SM_DICTIONARY_TB
set CONTENT = #{content,jdbcType=VARCHAR},
  PARENT_FIELD_NAME = #{parentFieldName,jdbcType=VARCHAR},
  PARENT_FIELD_CODE = #{parentFieldCode,jdbcType=VARCHAR}
where FIELD_NAME = #{fieldName,jdbcType=VARCHAR}
  and CODE = #{code,jdbcType=VARCHAR}

mapper.select:
	select  from SM_DICTIONARY_TB


      FIELD_NAME like '%'||#{fieldName,jdbcType=VARCHAR}||'%'


      AND CODE = #{code,jdbcType=VARCHAR}


      AND CONTENT like '%'||#{content,jdbcType=VARCHAR}||'%'


      AND PARENT_FIELD_NAME = #{parentFieldName,jdbcType=VARCHAR}


      AND PARENT_FIELD_CODE = #{parentFieldCode,jdbcType=VARCHAR}


	order by field_name,code
