mapper.insert:

insert into SM_ORGAN_DATA_TB (ORGAN_NO, SERVICE_ID, TMPBATCH_TB_ID, 
  TMPDATA_TB_ID, FLOW_TB_ID, ECMSERVICE_ID
  )
values (#{organNo,jdbcType=VARCHAR}, #{serviceId,jdbcType=DECIMAL}, #{tmpbatchTbId,jdbcType=DECIMAL}, 
  #{tmpdataTbId,jdbcType=DECIMAL}, #{flowTbId,jdbcType=DECIMAL}, #{ecmserviceId,jdbcType=VARCHAR}
  )

mapper.select:
	select  from SM_ORGAN_DATA_TB


      ORGAN_NO = #{organNo,jdbcType=VARCHAR}


      AND SERVICE_ID = #{serviceId,jdbcType=DECIMAL}


      AND TMPBATCH_TB_ID = #{tmpbatchTbId,jdbcType=DECIMAL}


      AND TMPDATA_TB_ID = #{tmpdataTbId,jdbcType=DECIMAL}


      AND FLOW_TB_ID = #{flowTbId,jdbcType=DECIMAL}


      AND ECMSERVICE_ID = #{ecmserviceId,jdbcType=VARCHAR}


	order by organ_no
