mapper.select:
select  from SM_DATASOURCE_SET_TB


     DATA_SOURCE_ID = #{dataSourceId,jdbcType=VARCHAR}


     AND GROUP_NAME = #{groupName,jdbcType=VARCHAR}


     AND MODE_CODE = #{modeCode,jdbcType=VARCHAR}


     AND INDEX_NAME = #{indexName,jdbcType=VARCHAR}


     AND FILE_PART_NAME = #{filePartName,jdbcType=VARCHAR}


     AND UA_IP = #{uaIp,jdbcType=VARCHAR}


     AND UA_PORT = #{uaPort,jdbcType=VARCHAR}


     AND DATA_DESC like '%'||#{dataDesc,jdbcType=VARCHAR}||'%'


     AND CUST_ID = #{custId,jdbcType=VARCHAR}


     AND SERVICE_ID = #{serviceId,jdbcType=VARCHAR}


    AND DATA_SOURCE_TYPE = #{dataSourceType,jdbcType=VARCHAR}
