mapper.select:
	select  from SM_SERVICE_REG_TB


      SERVICE_ID = #{serviceId,jdbcType=DECIMAL}


      AND SERVICE_NAME = #{serviceName,jdbcType=VARCHAR}


      AND SERVICE_IP = #{serviceIp,jdbcType=VARCHAR}


      AND SERVICE_PORT = #{servicePort,jdbcType=VARCHAR}


      AND SERVICE_TYPE = #{serviceType,jdbcType=VARCHAR}


      AND LOGIN_NAME = #{loginName,jdbcType=VARCHAR}


      AND LOGIN_PASS = #{loginPass,jdbcType=VARCHAR}


      AND IS_MONITOR = #{isMonitor,jdbcType=CHAR}


      AND LISTENER_POST = #{listenerPost,jdbcType=VARCHAR}


      AND GROUP_ID = #{groupId,jdbcType=DECIMAL}


      AND WORK_TYPE = #{workType,jdbcType=CHAR}
